import { NextResponse } from "next/server";
import { serviceContainer } from "@/lib/services/container";
import { databaseService } from "@/lib/database/database-service";
import { ProductEnrichmentService } from "@/lib/services/product-enrichment-service";
import { decryptSecret } from "@/lib/utils/crypto";
import {
    createErrorResponse,
    createSuccessResponse,
    createNotFoundResponse
} from "@/lib/utils/api-response";
import { type Product } from "@/lib/database/schema";
import type { eq as eqType } from "drizzle-orm";
import type { BetterSQLite3Database } from "drizzle-orm/better-sqlite3";
import * as schema from "@/lib/database/schema";

interface GeminiCredentials {
    apiKey: string;
    model?: string;
    enabled?: boolean;
    confidenceThreshold?: number;
}

type ProductUpdateData = Partial<Product>;
// Extract specific EnrichmentData type from Product if possible, or define compatible shape
type EnrichmentData = NonNullable<Product['enrichmentData']>;

/**
 * POST /api/v1/produits/[id]/retry-enrichment
 * Retry AI enrichment for a product that failed enrichment
 */
export async function POST(
    _request: Request,
    { params }: { params: { id: string } }
) {
    try {
        const { id } = await params;
        const authService = serviceContainer.getAuthService();
        const user = await authService.requireAuth();

        const productService = serviceContainer.getProductService();
        const product = await productService.getProduct(id, user.id);

        if (!product) {
            return createNotFoundResponse("Produit");
        }

        // Get Gemini credentials using the same pattern as automation.ts
        const db = await databaseService.getDb() as BetterSQLite3Database<typeof schema>;
        type QueryFn = typeof eqType;
        const cred = await db.query.integrationCredentials.findFirst({
            where: (t, { eq, and }) => and(
                eq(t.userId, user.id),
                eq(t.provider, "gemini")
            )
        });

        if (!cred?.credentials || !(cred.credentials as GeminiCredentials).enabled) {
            return NextResponse.json(
                { error: "L'enrichissement Gemini n'est pas activé" },
                { status: 400 }
            );
        }

        const apiKey = await decryptSecret((cred.credentials as GeminiCredentials).apiKey, user.id);
        const model = (cred.credentials as GeminiCredentials).model || "gemini-2.5-flash";

        // Get photo URLs for enrichment
        const photoUrls = (product.photoUrls as string[]) ||
            (product.photoUrl ? [product.photoUrl] : []);

        if (photoUrls.length === 0) {
            return NextResponse.json(
                { error: "Aucune photo disponible pour l'enrichissement" },
                { status: 400 }
            );
        }

        // Mark as pending first
        await productService.updateProduct(id, user.id, {
            enrichmentData: {
                ...(product.enrichmentData || {}),
                enrichmentStatus: 'pending',
                enrichedAt: new Date().toISOString(),
            } as EnrichmentData,
        } as ProductUpdateData);

        // Run enrichment
        const enrichmentService = new ProductEnrichmentService(apiKey, model);
        const result = await enrichmentService.enrichProduct(product.name, photoUrls);

        // Get confidence threshold from config
        const confidenceThreshold = (cred.credentials as GeminiCredentials).confidenceThreshold ?? 0.9;
        const isLowConfidence = result.confidence < confidenceThreshold;
        const newName = isLowConfidence ? `"${result.name}"` : result.name;

        // Update product with enrichment results
        await productService.updateProduct(id, user.id, {
            name: newName,
            brand: result.brand || product.brand,
            category: result.category || product.category,
            subcategory: result.subcategory || product.subcategory,
            url: result.url || product.url,
            description: result.description || product.description,
            enrichmentData: {
                confidence: result.confidence,
                originalUrl: result.url,
                source: result.source,
                modelUsed: model,
                enrichedAt: new Date().toISOString(),
                enrichmentStatus: 'done',
                vintedBrandId: result.vintedBrandId,
                vintedCatalogId: result.vintedCatalogId,
            } as EnrichmentData,
        } as ProductUpdateData);

        return createSuccessResponse({
            message: "Produit enrichi avec succès",
            result: {
                name: newName,
                confidence: result.confidence,
                brand: result.brand,
                category: result.category,
            },
        });
    } catch (error) {
        // Try to mark as failed
        try {
            const { id } = await params;
            const authService = serviceContainer.getAuthService();
            const user = await authService.requireAuth();
            const productService = serviceContainer.getProductService();

            await productService.updateProduct(id, user.id, {
                enrichmentData: {
                    enrichmentStatus: 'failed',
                    enrichedAt: new Date().toISOString(),
                    error: error instanceof Error ? error.message : 'Erreur inconnue',
                } as EnrichmentData,
            } as ProductUpdateData);
        } catch (_e) {
            // Ignore update errors
        }

        return createErrorResponse(error);
    }
}

"use client";

import { useState, useEffect, useCallback } from "react";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { toast } from "sonner";
import {
  User,
  Mail,
  Edit,
  Save,
  X,
  Lock,
  BarChart3,
  Calendar,
  Package,
  ShoppingBag,
} from "lucide-react";
import { Skeleton } from "@/components/ui/skeleton";
import { Separator } from "@/components/ui/separator";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { useFormatting } from "@/lib/hooks/use-formatting";

interface ProfileData {
  id: string;
  username: string;
  email: string | null;
  bio: string | null;
  avatar: string | null;
  language: string | null;
  theme: string | null;
  role: string;
  lastLoginAt: string | null;
  createdAt: string;
  updatedAt: string;
  stats?: {
    totalProducts: number;
    totalParcels: number;
    daysActive: number;
  };
}

interface ProfileClientProps {
  initialData?: ProfileData;
}

export function ProfileClient({ initialData }: ProfileClientProps) {
  const [profile, setProfile] = useState<ProfileData | null>(
    initialData || null,
  );
  const [editedProfile, setEditedProfile] = useState<Partial<ProfileData>>({});
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(!initialData);
  const [saving, setSaving] = useState(false);
  const [showPasswordDialog, setShowPasswordDialog] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [changingPassword, setChangingPassword] = useState(false);
  const { formatDateTime } = useFormatting();


  const getApiErrorMessage = (
    apiResponse: unknown,
    fallback = "Erreur inconnue",
  ) => {
    try {
      if (!apiResponse) return fallback;
      if (typeof apiResponse === "string") return apiResponse;

      const res = apiResponse as {
        error?: {
          message?: string;
          validationErrors?: Array<{ field?: string; message: string }>
        };
        success?: boolean;
        message?: string;
      };
      const err = (res.error || res) as any;
      if (!err) return fallback;
      if (typeof err === "string") return err;

      if (err.message) {
        // If validation errors exist, concatenate them
        if (
          Array.isArray(err.validationErrors) &&
          err.validationErrors.length
        ) {
          const details = err.validationErrors
            .map((v) => (v.field ? `${v.field}: ${v.message}` : v.message))
            .join("; ");
          return `${err.message} — ${details}`;
        }
        return err.message;
      }
      return JSON.stringify(err);
    } catch {
      return fallback;
    }
  };

  const loadProfile = useCallback(async () => {
    try {
      const response = await fetch("/api/v1/profile");
      const data = await response.json();
      if (data.success) {
        setProfile(data.data);
        setEditedProfile({
          email: data.data.email || "",
          bio: data.data.bio || "",
          avatar: data.data.avatar || "",
        });
      }
    } catch (_error) {
      toast.error("Erreur", {
        description: "Impossible de charger le profil",
      });
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (!initialData) {
      loadProfile();
    } else {
      setProfile(initialData);
      setEditedProfile({
        email: initialData.email || "",
        bio: initialData.bio || "",
        avatar: initialData.avatar || "",
      });
      setLoading(false);
    }

  }, [initialData, loadProfile]);

  const handleSave = async () => {
    setSaving(true);
    try {
      const response = await fetch("/api/v1/profile", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editedProfile),
      });

      const data = await response.json();
      if (data.success) {
        toast.success("Succès", {
          description: "Profil mis à jour avec succès",
        });
        setIsEditing(false);
        await loadProfile();
      } else {
        const message = getApiErrorMessage(
          data,
          "Erreur lors de la mise à jour",
        );
        throw new Error(message);
      }
    } catch (error) {
      toast.error("Erreur", {
        description:
          error instanceof Error
            ? error.message
            : "Erreur lors de la mise à jour",
      });
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditedProfile({
      email: profile?.email || "",
      bio: profile?.bio || "",
      avatar: profile?.avatar || "",
    });
  };

  const handlePasswordChange = async () => {
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast.error("Erreur", {
        description: "Les mots de passe ne correspondent pas",
      });
      return;
    }

    setChangingPassword(true);
    try {
      const response = await fetch("/api/v1/profile/change-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(passwordData),
      });

      const data = await response.json();
      if (data.success) {
        toast.success("Succès", {
          description: "Mot de passe changé avec succès",
        });
        setShowPasswordDialog(false);
        setPasswordData({
          currentPassword: "",
          newPassword: "",
          confirmPassword: "",
        });
      } else {
        const message = getApiErrorMessage(
          data,
          "Erreur lors du changement de mot de passe",
        );
        throw new Error(message);
      }
    } catch (error) {
      toast.error("Erreur", {
        description:
          error instanceof Error
            ? error.message
            : "Erreur lors du changement de mot de passe",
      });
    } finally {
      setChangingPassword(false);
    }
  };

  if (loading || !profile) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-12 w-full" />
        <Skeleton className="h-96 w-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Section 1 : Informations Personnelles */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <User className="h-6 w-6 text-primary" />
              <div>
                <CardTitle>Informations personnelles</CardTitle>
                <CardDescription>
                  Gérez vos informations de profil
                </CardDescription>
              </div>
            </div>
            {!isEditing ? (
              <Button
                onClick={() => setIsEditing(true)}
                variant="outline"
                size="sm"
              >
                <Edit className="h-4 w-4 mr-2" />
                Modifier
              </Button>
            ) : (
              <div className="flex space-x-2">
                <Button onClick={handleSave} disabled={saving} size="sm">
                  {saving ? (
                    "Sauvegarde..."
                  ) : (
                    <>
                      <Save className="h-4 w-4 mr-2" />
                      Sauvegarder
                    </>
                  )}
                </Button>
                <Button
                  onClick={handleCancel}
                  variant="outline"
                  disabled={saving}
                  size="sm"
                >
                  <X className="h-4 w-4 mr-2" />
                  Annuler
                </Button>
              </div>
            )}
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Username (lecture seule) */}
          <div className="space-y-2">
            <Label className="flex items-center space-x-2">
              <User className="h-4 w-4" />
              <span>Nom d'utilisateur</span>
            </Label>
            <Input value={profile.username} disabled />
            <p className="text-xs text-muted-foreground">
              Le nom d'utilisateur ne peut pas être modifié
            </p>
          </div>

          {/* Email */}
          <div className="space-y-2">
            <Label className="flex items-center space-x-2">
              <Mail className="h-4 w-4" />
              <span>Email</span>
            </Label>
            {isEditing ? (
              <Input
                type="email"
                value={editedProfile.email || ""}
                onChange={(e) =>
                  setEditedProfile((prev) => ({
                    ...prev,
                    email: e.target.value,
                  }))
                }
                placeholder="votre@email.com"
              />
            ) : (
              <Input value={profile.email || "Non renseigné"} disabled />
            )}
          </div>

          {/* Bio */}
          <div className="space-y-2">
            <Label>Biographie</Label>
            {isEditing ? (
              <Textarea
                value={editedProfile.bio || ""}
                onChange={(e) =>
                  setEditedProfile((prev) => ({ ...prev, bio: e.target.value }))
                }
                placeholder="Parlez-nous de vous..."
                rows={4}
                maxLength={500}
              />
            ) : (
              <Textarea
                value={profile.bio || "Aucune biographie renseignée"}
                disabled
                rows={4}
              />
            )}
            {isEditing && (
              <p className="text-xs text-muted-foreground">
                {(editedProfile.bio || "").length}/500 caractères
              </p>
            )}
          </div>

          {/* Avatar URL */}
          <div className="space-y-2">
            <Label>URL de l'avatar</Label>
            {isEditing ? (
              <Input
                type="url"
                value={editedProfile.avatar || ""}
                onChange={(e) =>
                  setEditedProfile((prev) => ({
                    ...prev,
                    avatar: e.target.value,
                  }))
                }
                placeholder="https://exemple.com/avatar.jpg"
              />
            ) : (
              <Input value={profile.avatar || "Aucun avatar"} disabled />
            )}
          </div>
        </CardContent>
      </Card>

      {/* Section 2 : Sécurité */}
      <Card>
        <CardHeader>
          <div className="flex items-center space-x-2">
            <Lock className="h-6 w-6 text-primary" />
            <div>
              <CardTitle>Sécurité du compte</CardTitle>
              <CardDescription>
                Gérez la sécurité de votre compte
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="font-medium">Mot de passe</p>
              <p className="text-sm text-muted-foreground">
                Changez votre mot de passe régulièrement pour plus de sécurité
              </p>
            </div>
            <Dialog
              open={showPasswordDialog}
              onOpenChange={setShowPasswordDialog}
            >
              <DialogTrigger asChild>
                <Button variant="outline">
                  <Lock className="h-4 w-4 mr-2" />
                  Changer
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Changer le mot de passe</DialogTitle>
                  <DialogDescription>
                    Entrez votre mot de passe actuel et choisissez un nouveau
                    mot de passe sécurisé.
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4 py-4">
                  <div className="space-y-2">
                    <Label htmlFor="currentPassword">Mot de passe actuel</Label>
                    <Input
                      id="currentPassword"
                      type="password"
                      value={passwordData.currentPassword}
                      onChange={(e) =>
                        setPasswordData((prev) => ({
                          ...prev,
                          currentPassword: e.target.value,
                        }))
                      }
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="newPassword">Nouveau mot de passe</Label>
                    <Input
                      id="newPassword"
                      type="password"
                      value={passwordData.newPassword}
                      onChange={(e) =>
                        setPasswordData((prev) => ({
                          ...prev,
                          newPassword: e.target.value,
                        }))
                      }
                    />
                    <p className="text-xs text-muted-foreground">
                      Minimum 8 caractères, avec majuscule, minuscule, chiffre
                      et caractère spécial
                    </p>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="confirmPassword">
                      Confirmer le mot de passe
                    </Label>
                    <Input
                      id="confirmPassword"
                      type="password"
                      value={passwordData.confirmPassword}
                      onChange={(e) =>
                        setPasswordData((prev) => ({
                          ...prev,
                          confirmPassword: e.target.value,
                        }))
                      }
                    />
                  </div>
                </div>
                <DialogFooter>
                  <Button
                    variant="outline"
                    onClick={() => setShowPasswordDialog(false)}
                    disabled={changingPassword}
                  >
                    Annuler
                  </Button>
                  <Button
                    onClick={handlePasswordChange}
                    disabled={changingPassword}
                  >
                    {changingPassword
                      ? "Changement..."
                      : "Changer le mot de passe"}
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </div>
        </CardContent>
      </Card>

      {/* Section 3 : Statistiques */}
      <Card>
        <CardHeader>
          <div className="flex items-center space-x-2">
            <BarChart3 className="h-6 w-6 text-primary" />
            <div>
              <CardTitle>Statistiques du compte</CardTitle>
              <CardDescription>
                Vue d'ensemble de votre activité
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="flex items-center space-x-3 p-4 border rounded-lg">
              <Package className="h-8 w-8 text-primary" />
              <div>
                <p className="text-2xl font-bold">
                  {profile.stats?.totalProducts || 0}
                </p>
                <p className="text-sm text-muted-foreground">Produits</p>
              </div>
            </div>
            <div className="flex items-center space-x-3 p-4 border rounded-lg">
              <ShoppingBag className="h-8 w-8 text-green-500" />
              <div>
                <p className="text-2xl font-bold">
                  {profile.stats?.totalParcels || 0}
                </p>
                <p className="text-sm text-muted-foreground">Parcelles</p>
              </div>
            </div>
            <div className="flex items-center space-x-3 p-4 border rounded-lg">
              <Calendar className="h-8 w-8 text-primary" />
              <div>
                <p className="text-2xl font-bold">
                  {profile.stats?.daysActive || 0}
                </p>
                <p className="text-sm text-muted-foreground">
                  Jours d'activité
                </p>
              </div>
            </div>
          </div>

          <Separator />

          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span className="text-muted-foreground">Compte créé le</span>
              <span className="font-medium">
                {formatDateTime(profile.createdAt) || "Non disponible"}
              </span>
            </div>
            <div className="flex justify-between text-sm">
              <span className="text-muted-foreground">Dernière connexion</span>
              <span className="font-medium">
                {formatDateTime(profile.lastLoginAt) || "Non disponible"}
              </span>
            </div>
            <div className="flex justify-between text-sm">
              <span className="text-muted-foreground">Rôle</span>
              <span className="font-medium capitalize">{profile.role}</span>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { useRouter, useSearchParams, usePathname } from "next/navigation";
import { useTheme } from "next-themes";
import { Tabs, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
    Card,
    CardContent,
    CardDescription,
    CardHeader,
    CardTitle,
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { toast } from "sonner";
import {
    User,
    Lock,
    BarChart3,
    Palette,
    Settings as SettingsIcon,
    Plug,
    Save,
    Package,
    ShoppingBag,
    Calendar,
    LogOut,
    Trash,
    Camera,
} from "lucide-react";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "@/components/ui/dialog";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { EnrichmentSettings } from "./enrichment-settings";
import { SuperbuyConnect } from "./superbuy-connect";
import { useFormatting } from "@/lib/hooks/use-formatting";
import { logger } from "@/lib/utils/logging/logger";

// Types
interface ProfileData {
    id: string;
    username: string;
    email: string | null;
    avatar: string | null;
    role: string;
    lastLoginAt: string | null;
    createdAt: string;
    updatedAt: string;
    stats?: {
        totalProducts: number;
        totalParcels: number;
        daysActive: number;
    };
}

interface SettingsData {
    theme: string;
    language: string;
    animations: boolean;
    preferences: {
        currency: string;
        weightUnit: string;
        dateFormat: string;
        autoExchangeRate: boolean;
        manualExchangeRate?: number;
    };
}

interface Session {
    id: string;
    deviceName: string | null;
    deviceType: string;
    ipAddress: string;
    lastActivityAt: string;
    isCurrent: boolean;
}

interface AccountSettingsClientProps {
    profileData: ProfileData;
    settingsData: SettingsData;
}

export function AccountSettingsClient({
    profileData: initialProfileData,
    settingsData: initialSettingsData,
}: AccountSettingsClientProps) {
    // Theme hook for visual application
    const { setTheme } = useTheme();
    const { formatDateTime } = useFormatting();

    // Hydration fix - only render tabs after client mount
    const [mounted, setMounted] = useState(false);
    useEffect(() => {
        setMounted(true);
    }, []);

    // Navigation hooks
    const router = useRouter();
    const pathname = usePathname();
    const searchParams = useSearchParams();

    // Tab state - synced with URL
    const activeTab = searchParams.get("tab") || "profile";

    const handleTabChange = (value: string) => {
        const params = new URLSearchParams(searchParams.toString());
        params.set("tab", value);
        // Using replace to avoid polluting history with every tab switch, or push if preferred
        router.replace(`${pathname}?${params.toString()}`);
    };

    // Profile state
    const [profile, setProfile] = useState<ProfileData>(initialProfileData);
    const [editedUsername, setEditedUsername] = useState(initialProfileData.username);
    const [editedAvatar, setEditedAvatar] = useState(initialProfileData.avatar || "");
    const [savingProfile, setSavingProfile] = useState(false);

    // Password state
    const [showPasswordDialog, setShowPasswordDialog] = useState(false);
    const [passwordData, setPasswordData] = useState({
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
    });
    const [changingPassword, setChangingPassword] = useState(false);

    // Settings state
    const [settings, setSettings] = useState<SettingsData>(initialSettingsData);
    const [saving, setSaving] = useState(false);

    // Sessions state
    const [sessions, setSessions] = useState<Session[]>([]);
    const [sessionsLoading, setSessionsLoading] = useState(false);
    const [showDeleteAllDialog, setShowDeleteAllDialog] = useState(false);
    const [deletingAll, setDeletingAll] = useState(false);
    const [sessionToDelete, setSessionToDelete] = useState<Session | null>(null);
    const [deletingSessionId, setDeletingSessionId] = useState<string | null>(null);

    // Helper function
    const getApiErrorMessage = (apiResponse: unknown, fallback = "Erreur inconnue") => {
        try {
            if (!apiResponse) return fallback;
            if (typeof apiResponse === "string") return apiResponse;
            const res = apiResponse as {
                error?: {
                    message?: string;
                    validationErrors?: Array<{ field?: string; message: string }>
                };
                success?: boolean;
                message?: string;
            };
            const err = res.error || res;
            if (!err) return fallback;
            if (typeof err === "string") return err;
            if (err.message) {
                const errorData = err as any;
                if (Array.isArray(errorData.validationErrors) && errorData.validationErrors.length) {
                    const details = errorData.validationErrors
                        .map((v) => (v.field ? `${v.field}: ${v.message}` : v.message))
                        .join("; ");
                    return `${err.message} — ${details}`;
                }
                return err.message;
            }
            return JSON.stringify(err);
        } catch {
            return fallback;
        }
    };

    // Profile handlers
    const handleSaveProfile = async () => {
        setSavingProfile(true);
        try {
            const response = await fetch("/api/v1/profile", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    username: editedUsername,
                    avatar: editedAvatar || null,
                }),
            });

            const data = await response.json();
            if (data.success) {
                setProfile((prev) => ({
                    ...prev,
                    username: editedUsername,
                    avatar: editedAvatar || null,
                }));
                toast.success("Profil mis à jour avec succès");
            } else {
                throw new Error(getApiErrorMessage(data, "Erreur lors de la mise à jour"));
            }
        } catch (error) {
            toast.error(error instanceof Error ? error.message : "Erreur lors de la mise à jour");
        } finally {
            setSavingProfile(false);
        }
    };

    const handlePasswordChange = async () => {
        if (passwordData.newPassword !== passwordData.confirmPassword) {
            toast.error("Les mots de passe ne correspondent pas");
            return;
        }

        if (passwordData.newPassword.length < 8) {
            toast.error("Le mot de passe doit contenir au moins 8 caractères");
            return;
        }

        setChangingPassword(true);
        try {
            const response = await fetch("/api/v1/profile/change-password", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(passwordData),
            });

            const data = await response.json();
            if (data.success) {
                toast.success("Mot de passe changé avec succès");
                setShowPasswordDialog(false);
                setPasswordData({ currentPassword: "", newPassword: "", confirmPassword: "" });
            } else {
                throw new Error(getApiErrorMessage(data, "Erreur lors du changement de mot de passe"));
            }
        } catch (error) {
            toast.error(error instanceof Error ? error.message : "Erreur lors du changement de mot de passe");
        } finally {
            setChangingPassword(false);
        }
    };

    // Settings handlers
    const saveSettings = async (partialSettings: Partial<SettingsData>) => {
        setSaving(true);
        try {
            const response = await fetch("/api/v1/settings", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(partialSettings),
            });

            const data = await response.json();
            if (data.success) {
                toast.success("Paramètres sauvegardés");
            } else {
                throw new Error(getApiErrorMessage(data, "Erreur lors de la sauvegarde"));
            }
        } catch (error) {
            toast.error(error instanceof Error ? error.message : "Erreur lors de la sauvegarde");
        } finally {
            setSaving(false);
        }
    };

    const handleThemeChange = (theme: string) => {
        setSettings((prev) => ({ ...prev, theme }));
        setTheme(theme); // Apply theme visually via next-themes
        saveSettings({ theme });
    };

    const handleLanguageChange = (language: string) => {
        setSettings((prev) => ({ ...prev, language }));
        saveSettings({ language });
    };

    const handleAnimationsChange = (animations: boolean) => {
        setSettings((prev) => ({ ...prev, animations }));
        saveSettings({ animations });
    };

    const handlePreferenceChange = (key: keyof SettingsData["preferences"], value: unknown) => {
        setSettings((prev) => ({
            ...prev,
            preferences: { ...prev.preferences, [key]: value },
        }));
    };

    const savePreferences = () => {
        saveSettings({ preferences: settings.preferences });
    };

    // Sessions handlers
    const fetchSessions = useCallback(async () => {
        setSessionsLoading(true);
        try {
            const res = await fetch("/api/v1/sessions");
            const json = await res.json();
            if (json.success) {
                setSessions(json.data?.sessions || []);
            }
        } catch (err) {
            logger.error("Error fetching sessions", { error: err });
        } finally {
            setSessionsLoading(false);
        }
    }, []);

    const handleDeleteSession = async (id: string) => {
        setDeletingSessionId(id);
        try {
            const res = await fetch(`/api/v1/sessions/${id}`, { method: "DELETE" });
            const json = await res.json();
            if (json.success) {
                toast.success("Session supprimée");
                await fetchSessions();
            } else {
                throw new Error(json.error || "Erreur lors de la suppression");
            }
        } catch (err) {
            toast.error(err instanceof Error ? err.message : String(err));
        } finally {
            setDeletingSessionId(null);
            setSessionToDelete(null);
        }
    };

    const handleDeleteAllSessions = async () => {
        setDeletingAll(true);
        try {
            const res = await fetch("/api/v1/sessions", { method: "DELETE" });
            const json = await res.json();
            if (json.success) {
                toast.success("Sessions terminées");
                await fetchSessions();
                setShowDeleteAllDialog(false);
            } else {
                throw new Error(json.error || "Erreur lors de la suppression");
            }
        } catch (err) {
            toast.error(err instanceof Error ? err.message : String(err));
        } finally {
            setDeletingAll(false);
        }
    };

    useEffect(() => {
        fetchSessions();
    }, [fetchSessions]);

    const hasProfileChanges = editedUsername !== profile.username || editedAvatar !== (profile.avatar || "");

    // Prevent hydration mismatch - show skeleton until client-side mounted
    if (!mounted) {
        return (
            <div className="space-y-6">
                <Skeleton className="h-12 w-full" />
                <Skeleton className="h-96 w-full" />
            </div>
        );
    }

    return (
        <>
            <Tabs value={activeTab} onValueChange={handleTabChange} className="w-full">
                <TabsList className="grid w-full grid-cols-5 mb-6">
                    <TabsTrigger value="profile" className="flex items-center gap-2" data-testid="tab-profile">
                        <User className="h-4 w-4" />
                        <span className="hidden sm:inline">Profil</span>
                    </TabsTrigger>
                    <TabsTrigger value="stats" className="flex items-center gap-2" data-testid="tab-stats">
                        <BarChart3 className="h-4 w-4" />
                        <span className="hidden sm:inline">Statistiques</span>
                    </TabsTrigger>
                    <TabsTrigger value="appearance" className="flex items-center gap-2" data-testid="tab-appearance">
                        <Palette className="h-4 w-4" />
                        <span className="hidden sm:inline">Apparence</span>
                    </TabsTrigger>
                    <TabsTrigger value="preferences" className="flex items-center gap-2" data-testid="tab-preferences">
                        <SettingsIcon className="h-4 w-4" />
                        <span className="hidden sm:inline">Préférences</span>
                    </TabsTrigger>
                    <TabsTrigger value="integrations" className="flex items-center gap-2" data-testid="tab-integrations">
                        <Plug className="h-4 w-4" />
                        <span className="hidden sm:inline">Intégrations</span>
                    </TabsTrigger>
                </TabsList>

                {/* Profile Tab */}
                {activeTab === "profile" && (
                    <div className="space-y-6 mt-2">
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center gap-2">
                                    <User className="h-5 w-5 text-primary" />
                                    Informations du profil
                                </CardTitle>
                                <CardDescription>
                                    Modifiez votre nom d'utilisateur et votre avatar
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-6">
                                {/* Avatar Section */}
                                <div className="flex items-center gap-6">
                                    <Avatar className="h-20 w-20">
                                        <AvatarImage src={editedAvatar || undefined} alt={profile.username} />
                                        <AvatarFallback className="text-2xl bg-gradient-to-br from-primary to-primary/60 text-primary-foreground">
                                            {profile.username.slice(0, 2).toUpperCase()}
                                        </AvatarFallback>
                                    </Avatar>
                                    <div className="flex-1 space-y-2">
                                        <Label htmlFor="avatar-url" className="flex items-center gap-2">
                                            <Camera className="h-4 w-4" />
                                            URL de l'avatar
                                        </Label>
                                        <Input
                                            id="avatar-url"
                                            type="url"
                                            value={editedAvatar}
                                            onChange={(e) => setEditedAvatar(e.target.value)}
                                            placeholder="https://exemple.com/avatar.jpg"
                                        />
                                    </div>
                                </div>

                                <Separator />

                                {/* Username */}
                                <div className="space-y-2">
                                    <Label htmlFor="username" className="flex items-center gap-2">
                                        <User className="h-4 w-4" />
                                        Nom d'utilisateur
                                    </Label>
                                    <Input
                                        id="username"
                                        value={editedUsername}
                                        onChange={(e) => setEditedUsername(e.target.value)}
                                        placeholder="johndoe"
                                    />
                                    <p className="text-xs text-muted-foreground">
                                        Ce nom sera visible dans l'application
                                    </p>
                                </div>

                                {/* Save Button */}
                                {hasProfileChanges && (
                                    <Button onClick={handleSaveProfile} disabled={savingProfile} className="w-full">
                                        {savingProfile ? "Sauvegarde..." : (
                                            <>
                                                <Save className="h-4 w-4 mr-2" />
                                                Sauvegarder les modifications
                                            </>
                                        )}
                                    </Button>
                                )}
                            </CardContent>
                        </Card>

                        {/* Security Card */}
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center gap-2">
                                    <Lock className="h-5 w-5 text-primary" />
                                    Sécurité
                                </CardTitle>
                                <CardDescription>
                                    Gérez la sécurité de votre compte
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-4">
                                <div className="flex items-center justify-between">
                                    <div>
                                        <p className="font-medium">Mot de passe</p>
                                        <p className="text-sm text-muted-foreground">
                                            Changez votre mot de passe régulièrement
                                        </p>
                                    </div>
                                    <Dialog open={showPasswordDialog} onOpenChange={setShowPasswordDialog}>
                                        <DialogTrigger asChild>
                                            <Button variant="outline">
                                                <Lock className="h-4 w-4 mr-2" />
                                                Changer
                                            </Button>
                                        </DialogTrigger>
                                        <DialogContent>
                                            <DialogHeader>
                                                <DialogTitle>Changer le mot de passe</DialogTitle>
                                                <DialogDescription>
                                                    Entrez votre mot de passe actuel et choisissez-en un nouveau.
                                                </DialogDescription>
                                            </DialogHeader>
                                            <div className="space-y-4 py-4">
                                                <div className="space-y-2">
                                                    <Label htmlFor="currentPassword">Mot de passe actuel</Label>
                                                    <Input
                                                        id="currentPassword"
                                                        type="password"
                                                        value={passwordData.currentPassword}
                                                        onChange={(e) => setPasswordData((prev) => ({ ...prev, currentPassword: e.target.value }))}
                                                    />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="newPassword">Nouveau mot de passe</Label>
                                                    <Input
                                                        id="newPassword"
                                                        type="password"
                                                        value={passwordData.newPassword}
                                                        onChange={(e) => setPasswordData((prev) => ({ ...prev, newPassword: e.target.value }))}
                                                    />
                                                    <p className="text-xs text-muted-foreground">
                                                        Minimum 8 caractères
                                                    </p>
                                                </div>
                                                <div className="space-y-2">
                                                    <Label htmlFor="confirmPassword">Confirmer</Label>
                                                    <Input
                                                        id="confirmPassword"
                                                        type="password"
                                                        value={passwordData.confirmPassword}
                                                        onChange={(e) => setPasswordData((prev) => ({ ...prev, confirmPassword: e.target.value }))}
                                                    />
                                                </div>
                                            </div>
                                            <DialogFooter>
                                                <Button variant="outline" onClick={() => setShowPasswordDialog(false)} disabled={changingPassword}>
                                                    Annuler
                                                </Button>
                                                <Button onClick={handlePasswordChange} disabled={changingPassword}>
                                                    {changingPassword ? "Changement..." : "Changer"}
                                                </Button>
                                            </DialogFooter>
                                        </DialogContent>
                                    </Dialog>
                                </div>

                                <Separator />

                                {/* Sessions Management */}
                                <div className="space-y-4">
                                    <div className="flex items-center justify-between">
                                        <div>
                                            <p className="font-medium">Sessions actives</p>
                                            <p className="text-sm text-muted-foreground">
                                                Gérez vos sessions sur différents appareils
                                            </p>
                                        </div>
                                        <Button variant="outline" onClick={() => setShowDeleteAllDialog(true)}>
                                            <LogOut className="h-4 w-4 mr-2" />
                                            Terminer les autres
                                        </Button>
                                    </div>

                                    {sessionsLoading ? (
                                        <div className="space-y-2">
                                            <Skeleton className="h-16 w-full" />
                                            <Skeleton className="h-16 w-full" />
                                        </div>
                                    ) : sessions.length === 0 ? (
                                        <p className="text-sm text-muted-foreground">Aucune session active</p>
                                    ) : (
                                        <div className="space-y-2">
                                            {sessions.map((s) => (
                                                <div key={s.id} className="flex items-center justify-between p-3 border rounded-lg">
                                                    <div>
                                                        <div className="font-medium">{s.deviceName || "Appareil inconnu"}</div>
                                                        <div className="text-sm text-muted-foreground">
                                                            {s.deviceType} • {s.ipAddress}
                                                        </div>
                                                        <div className="text-xs text-muted-foreground">
                                                            Dernière activité: {new Date(s.lastActivityAt).toLocaleString()}
                                                        </div>
                                                    </div>
                                                    {s.isCurrent ? (
                                                        <span className="px-2 py-1 text-xs bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200 rounded">
                                                            Actuelle
                                                        </span>
                                                    ) : (
                                                        <Button variant="ghost" size="sm" onClick={() => setSessionToDelete(s)}>
                                                            <Trash className="w-4 h-4" />
                                                        </Button>
                                                    )}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                )}

                {activeTab === "stats" && (
                    <div className="space-y-6 mt-2">
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center gap-2">
                                    <BarChart3 className="h-5 w-5 text-primary" />
                                    Statistiques du compte
                                </CardTitle>
                                <CardDescription>
                                    Vue d'ensemble de votre activité
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-6">
                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                    <div className="flex items-center gap-4 p-4 border rounded-lg bg-gradient-to-br from-blue-50 to-transparent dark:from-blue-950/20">
                                        <Package className="h-10 w-10 text-primary" />
                                        <div>
                                            <p className="text-3xl font-bold">{profile.stats?.totalProducts || 0}</p>
                                            <p className="text-sm text-muted-foreground">Produits</p>
                                        </div>
                                    </div>
                                    <div className="flex items-center gap-4 p-4 border rounded-lg bg-gradient-to-br from-green-50 to-transparent dark:from-green-950/20">
                                        <ShoppingBag className="h-10 w-10 text-green-500" />
                                        <div>
                                            <p className="text-3xl font-bold">{profile.stats?.totalParcels || 0}</p>
                                            <p className="text-sm text-muted-foreground">Parcelles</p>
                                        </div>
                                    </div>
                                    <div className="flex items-center gap-4 p-4 border rounded-lg bg-gradient-to-br from-purple-50 to-transparent dark:from-purple-950/20">
                                        <Calendar className="h-10 w-10 text-primary" />
                                        <div>
                                            <p className="text-3xl font-bold">{profile.stats?.daysActive || 0}</p>
                                            <p className="text-sm text-muted-foreground">Jours d'activité</p>
                                        </div>
                                    </div>
                                </div>

                                <Separator />

                                <div className="space-y-3">
                                    <div className="flex justify-between text-sm">
                                        <span className="text-muted-foreground">Compte créé le</span>
                                        <span className="font-medium">{formatDateTime(profile.createdAt) || "Non disponible"}</span>
                                    </div>
                                    <div className="flex justify-between text-sm">
                                        <span className="text-muted-foreground">Dernière connexion</span>
                                        <span className="font-medium">{formatDateTime(profile.lastLoginAt) || "Non disponible"}</span>
                                    </div>
                                    <div className="flex justify-between text-sm">
                                        <span className="text-muted-foreground">Rôle</span>
                                        <span className="font-medium capitalize">{profile.role}</span>
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                )}

                {activeTab === "appearance" && (
                    <div className="space-y-6 mt-2">
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center gap-2">
                                    <Palette className="h-5 w-5 text-primary" />
                                    Apparence
                                </CardTitle>
                                <CardDescription>
                                    Personnalisez l'interface selon vos préférences
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-6">
                                <div className="space-y-2">
                                    <Label>Thème</Label>
                                    <Select value={settings.theme} onValueChange={handleThemeChange}>
                                        <SelectTrigger>
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            <SelectItem value="light">Clair</SelectItem>
                                            <SelectItem value="dark">Sombre</SelectItem>
                                            <SelectItem value="system">Système</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>

                                <div className="space-y-2">
                                    <Label>Langue</Label>
                                    <Select value={settings.language} onValueChange={handleLanguageChange}>
                                        <SelectTrigger>
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            <SelectItem value="fr">Français</SelectItem>
                                            <SelectItem value="en">English</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>

                                <div className="flex items-center justify-between">
                                    <div>
                                        <Label>Animations</Label>
                                        <p className="text-sm text-muted-foreground">
                                            Activer les animations d'interface
                                        </p>
                                    </div>
                                    <Switch
                                        checked={settings.animations}
                                        onCheckedChange={handleAnimationsChange}
                                        disabled={saving}
                                    />
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                )}

                {activeTab === "preferences" && (
                    <div className="space-y-6 mt-2">
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center gap-2">
                                    <SettingsIcon className="h-5 w-5 text-primary" />
                                    Préférences métier
                                </CardTitle>
                                <CardDescription>
                                    Configurez les paramètres par défaut
                                </CardDescription>
                            </CardHeader>
                            <CardContent className="space-y-6">
                                <div className="space-y-2">
                                    <Label>Devise par défaut</Label>
                                    <Select
                                        value={settings.preferences.currency}
                                        onValueChange={(value) => handlePreferenceChange("currency", value)}
                                    >
                                        <SelectTrigger>
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            <SelectItem value="EUR">Euro (€)</SelectItem>
                                            <SelectItem value="USD">Dollar US ($)</SelectItem>
                                            <SelectItem value="CNY">Yuan chinois (¥)</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>

                                <div className="space-y-2">
                                    <Label>Unité de poids</Label>
                                    <Select
                                        value={settings.preferences.weightUnit}
                                        onValueChange={(value) => handlePreferenceChange("weightUnit", value)}
                                    >
                                        <SelectTrigger>
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            <SelectItem value="g">Grammes (g)</SelectItem>
                                            <SelectItem value="kg">Kilogrammes (kg)</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>

                                <div className="space-y-2">
                                    <Label>Format de date</Label>
                                    <Select
                                        value={settings.preferences.dateFormat}
                                        onValueChange={(value) => handlePreferenceChange("dateFormat", value)}
                                    >
                                        <SelectTrigger>
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            <SelectItem value="DD/MM/YYYY">JJ/MM/AAAA</SelectItem>
                                            <SelectItem value="MM/DD/YYYY">MM/JJ/AAAA</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>

                                <div className="flex items-center justify-between">
                                    <div>
                                        <Label>Taux de change automatique</Label>
                                        <p className="text-sm text-muted-foreground">
                                            Mise à jour automatique des taux
                                        </p>
                                    </div>
                                    <Switch
                                        checked={settings.preferences.autoExchangeRate}
                                        onCheckedChange={(value) => handlePreferenceChange("autoExchangeRate", value)}
                                    />
                                </div>

                                {!settings.preferences.autoExchangeRate && settings.preferences.currency !== "EUR" && (
                                    <div className="space-y-2 p-4 rounded-lg bg-muted/50 border">
                                        <Label>Taux de change manuel (EUR → {settings.preferences.currency})</Label>
                                        <Input
                                            type="number"
                                            step="0.0001"
                                            min="0.0001"
                                            value={settings.preferences.manualExchangeRate || 1}
                                            onChange={(e) => handlePreferenceChange("manualExchangeRate", parseFloat(e.target.value) || 1)}
                                            placeholder="ex: 1.08"
                                            className="max-w-xs"
                                        />
                                        <p className="text-xs text-muted-foreground">
                                            1 EUR = {settings.preferences.manualExchangeRate || 1} {settings.preferences.currency}
                                        </p>
                                    </div>
                                )}

                                <Button onClick={savePreferences} disabled={saving} className="w-full">
                                    {saving ? "Sauvegarde..." : "Sauvegarder les préférences"}
                                </Button>
                            </CardContent>
                        </Card>
                    </div>
                )}

                {activeTab === "integrations" && (
                    <div className="space-y-6 mt-2">
                        <SuperbuyConnect />
                        <EnrichmentSettings />

                        <Card>
                            <CardHeader>
                                <CardTitle>Autres Intégrations</CardTitle>
                                <CardDescription>
                                    Connectez vos comptes marketplace
                                </CardDescription>
                            </CardHeader>
                            <CardContent>
                                <div className="flex flex-col items-center justify-center py-12 text-center">
                                    <Plug className="h-12 w-12 text-muted-foreground mb-4" />
                                    <h3 className="text-lg font-semibold mb-2">Bientôt disponible</h3>
                                    <p className="text-sm text-muted-foreground max-w-md">
                                        Les intégrations avec Vinted, eBay et d'autres plateformes seront bientôt disponibles.
                                    </p>
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                )}
            </Tabs>

            {/* Delete All Sessions Dialog */}
            <Dialog open={showDeleteAllDialog} onOpenChange={setShowDeleteAllDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle>Terminer les autres sessions</DialogTitle>
                        <DialogDescription>
                            Cette action déconnectera vos sessions sur d'autres appareils.
                        </DialogDescription>
                    </DialogHeader>
                    <div className="py-4">
                        <p className="text-sm text-muted-foreground">
                            Sessions à terminer: {sessions.filter((s) => !s.isCurrent).length}
                        </p>
                    </div>
                    <DialogFooter>
                        <Button variant="ghost" onClick={() => setShowDeleteAllDialog(false)}>
                            Annuler
                        </Button>
                        <Button onClick={handleDeleteAllSessions} disabled={deletingAll}>
                            {deletingAll ? "Suppression..." : "Terminer"}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Single Session Delete Dialog */}
            <Dialog open={!!sessionToDelete} onOpenChange={(open) => !open && setSessionToDelete(null)}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle>Terminer la session</DialogTitle>
                        <DialogDescription>
                            Cette action est irréversible.
                        </DialogDescription>
                    </DialogHeader>
                    {sessionToDelete && (
                        <div className="py-4">
                            <div className="font-medium">{sessionToDelete.deviceName || "Inconnu"}</div>
                            <div className="text-sm text-muted-foreground">
                                {sessionToDelete.deviceType} • {sessionToDelete.ipAddress}
                            </div>
                        </div>
                    )}
                    <DialogFooter>
                        <Button variant="ghost" onClick={() => setSessionToDelete(null)}>
                            Annuler
                        </Button>
                        <Button
                            onClick={() => sessionToDelete && handleDeleteSession(sessionToDelete.id)}
                            disabled={!!deletingSessionId}
                        >
                            {deletingSessionId ? "Suppression..." : "Terminer"}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>
        </>
    );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { useRouter, useSearchParams, usePathname } from "next/navigation";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { toast } from "sonner";
import {
  Palette,
  Shield,
  Settings as SettingsIcon,
  Plug,
  LogOut,
  Trash,
} from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogFooter,
} from "@/components/ui/dialog";
import { Skeleton } from "@/components/ui/skeleton";
import { SuperbuyConnect } from "./superbuy-connect";

interface SettingsData {
  theme: string;
  language: string;
  animations: boolean;
  preferences: {
    currency: string;
    weightUnit: string;
    dateFormat: string;
    autoExchangeRate: boolean;
  };
}

interface Session {
  id: string;
  deviceName: string | null;
  deviceType: string;
  ipAddress: string;
  lastActivityAt: string;
  isCurrent: boolean;
}

interface SettingsClientProps {
  initialData?: SettingsData;
}

export function SettingsClient({ initialData }: SettingsClientProps) {
  const router = useRouter();
  const pathname = usePathname();
  const searchParams = useSearchParams();

  // Get active tab from URL or default to 'appearance'
  const activeTab = searchParams.get("tab") || "appearance";

  const handleTabChange = (value: string) => {
    const params = new URLSearchParams(searchParams.toString());
    params.set("tab", value);
    // Use replace to update URL without adding to history stack, or push if history desired
    router.replace(`${pathname}?${params.toString()}`);
  };

  const [settings, setSettings] = useState<SettingsData | null>(
    initialData || null,
  );
  const [loading, setLoading] = useState(!initialData);
  const [saving, setSaving] = useState(false);
  const [sessionsLoading, setSessionsLoading] = useState(false);
  const [sessions, setSessions] = useState<Array<Session>>([]);
  const [showDeleteAllDialog, setShowDeleteAllDialog] = useState(false);
  const [deletingAll, setDeletingAll] = useState(false);
  const [sessionToDelete, setSessionToDelete] = useState<Session | null>(null);
  const [deletingSessionId, setDeletingSessionId] = useState<string | null>(
    null,
  );


  const getApiErrorMessage = (
    apiResponse: unknown,
    fallback = "Erreur inconnue",
  ) => {
    try {
      if (!apiResponse) return fallback;
      if (typeof apiResponse === "string") return apiResponse;
      const res = apiResponse as {
        error?: {
          message?: string;
          validationErrors?: Array<{ field?: string; message: string }>
        };
        success?: boolean;
        message?: string;
      };
      const err = res.error || res;
      if (!err) return fallback;
      if (typeof err === "string") return err;
      if (err.message) {
        const errorData = err as any;
        return (
          Array.isArray(errorData.validationErrors) &&
          errorData.validationErrors.length
        ) ? (
          <div className="text-sm">
            {errorData.message}
            <ul className="list-disc pl-4 mt-1 space-y-1">
              {errorData.validationErrors.map((v: { field?: string; message: string }, i: number) => (
                <li key={i}>{v.field ? `${v.field}: ${v.message}` : v.message}</li>
              ))}
            </ul>
          </div>
        ) : errorData.message;
      }
      return JSON.stringify(err);
    } catch {
      return fallback;
    }
  };

  const loadSettings = useCallback(async () => {
    try {
      const response = await fetch("/api/v1/settings");
      const data = await response.json();
      if (data.success) {
        setSettings(data.data);
      }
    } catch (_error) {
      toast.error("Erreur", {
        description: "Impossible de charger les paramètres",
      });
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (!initialData) {
      loadSettings();
    } else {
      setSettings(initialData);
      setLoading(false);
    }

  }, [initialData, loadSettings]);

  const saveSettings = async (partialSettings: Partial<SettingsData>) => {
    setSaving(true);
    try {
      const response = await fetch("/api/v1/settings", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(partialSettings),
      });

      const data = await response.json();
      if (data.success) {
        toast.success("Succès", {
          description: "Paramètres sauvegardés avec succès",
        });
        // Recharger les settings
        await loadSettings();
      } else {
        const message = getApiErrorMessage(
          data,
          "Erreur lors de la sauvegarde",
        );
        throw new Error(message);
      }
    } catch (error) {
      toast.error("Erreur", {
        description:
          error instanceof Error
            ? error.message
            : "Erreur lors de la sauvegarde",
      });
    } finally {
      setSaving(false);
    }
  };

  const handleThemeChange = (theme: string) => {
    setSettings((prev) => (prev ? { ...prev, theme } : null));
    saveSettings({ theme });
  };

  const handleLanguageChange = (language: string) => {
    setSettings((prev) => (prev ? { ...prev, language } : null));
    saveSettings({ language });
  };

  const handleAnimationsChange = (animations: boolean) => {
    setSettings((prev) => (prev ? { ...prev, animations } : null));
    saveSettings({ animations });
  };

  const handlePreferenceChange = (
    key: keyof SettingsData["preferences"],
    value: unknown,
  ) => {
    setSettings((prev) =>
      prev
        ? {
          ...prev,
          preferences: { ...prev.preferences, [key]: value },
        }
        : null,
    );
  };

  const savePreferences = () => {
    if (settings) {
      saveSettings({ preferences: settings.preferences });
    }
  };

  // --- Sessions management ---
  const fetchSessions = useCallback(async () => {
    setSessionsLoading(true);
    try {
      const res = await fetch("/api/v1/sessions");
      const json = await res.json();
      if (json.success) {
        setSessions(json.data.sessions || []);
      } else {
        throw new Error(json.error || "Impossible de charger les sessions");
      }
    } catch (err) {
      toast.error("Erreur", {
        description: err instanceof Error ? err.message : String(err),
      });
    } finally {
      setSessionsLoading(false);
    }
  }, []);

  const handleDeleteSession = async (id: string) => {
    setDeletingSessionId(id);
    try {
      const res = await fetch(`/api/v1/sessions/${id}`, { method: "DELETE" });
      const json = await res.json();
      if (json.success) {
        toast.success("Succès", {
          description: json.data?.message || "Session supprimée",
        });
        await fetchSessions();
      } else {
        throw new Error(json.error || "Erreur lors de la suppression");
      }
    } catch (err) {
      toast.error("Erreur", {
        description: err instanceof Error ? err.message : String(err),
      });
    } finally {
      setDeletingSessionId(null);
      setSessionToDelete(null);
    }
  };

  useEffect(() => {
    // Pré-charger les sessions lorsque l'onglet sécurité est accessible
    // (On load initial pour simplicité)
    fetchSessions();

  }, [fetchSessions]);

  function SessionsList() {
    if (sessionsLoading) {
      return (
        <div className="space-y-2">
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
        </div>
      );
    }

    if (!sessions.length) {
      return (
        <p className="text-sm text-muted-foreground">
          Aucune session active trouvée.
        </p>
      );
    }

    return (
      <div className="space-y-2">
        {sessions.map((s) => (
          <div
            key={s.id}
            className="flex items-center justify-between p-3 border rounded"
          >
            <div>
              <div className="font-medium">{s.deviceName || "Inconnu"}</div>
              <div className="text-sm text-muted-foreground">
                {s.deviceType} • {s.ipAddress}
              </div>
              <div className="text-xs text-muted-foreground">
                Dernière activité: {new Date(s.lastActivityAt).toLocaleString()}
              </div>
            </div>
            <div className="flex items-center gap-2">
              {s.isCurrent ? (
                <span className="px-2 py-1 text-xs bg-success/15 text-success rounded font-medium border border-success/20">
                  Actuelle
                </span>
              ) : (
                <>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setSessionToDelete(s)}
                  >
                    <Trash className="w-4 h-4 mr-1" />
                    Terminer
                  </Button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    );
  }

  // Session delete confirmation dialog
  const confirmDeleteSession = async () => {
    if (!sessionToDelete) return;
    await handleDeleteSession(sessionToDelete.id);
  };

  if (loading || !settings) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-12 w-full" />
        <Skeleton className="h-96 w-full" />
      </div>
    );
  }

  return (
    <>
      <Tabs value={activeTab} onValueChange={handleTabChange} className="w-full">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="appearance" className="flex items-center gap-2">
            <Palette className="h-4 w-4" />
            <span className="hidden sm:inline">Apparence</span>
          </TabsTrigger>
          <TabsTrigger value="security" className="flex items-center gap-2">
            <Shield className="h-4 w-4" />
            <span className="hidden sm:inline">Sécurité</span>
          </TabsTrigger>
          <TabsTrigger value="preferences" className="flex items-center gap-2">
            <SettingsIcon className="h-4 w-4" />
            <span className="hidden sm:inline">Préférences</span>
          </TabsTrigger>
          <TabsTrigger value="integrations" className="flex items-center gap-2">
            <Plug className="h-4 w-4" />
            <span className="hidden sm:inline">Intégrations</span>
          </TabsTrigger>
        </TabsList>

        {/* Onglet Apparence */}
        <TabsContent value="appearance" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Apparence de l'interface</CardTitle>
              <CardDescription>
                Personnalisez l'apparence de l'application selon vos préférences
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <Label>Thème</Label>
                <Select
                  value={settings.theme}
                  onValueChange={handleThemeChange}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="light">Clair</SelectItem>
                    <SelectItem value="dark">Sombre</SelectItem>
                    <SelectItem value="system">Système</SelectItem>
                  </SelectContent>
                </Select>
                <p className="text-sm text-muted-foreground">
                  Choisissez le thème de couleur de l'interface
                </p>
              </div>

              <div className="space-y-2">
                <Label>Langue</Label>
                <Select
                  value={settings.language}
                  onValueChange={handleLanguageChange}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="fr">Français</SelectItem>
                    <SelectItem value="en">English</SelectItem>
                  </SelectContent>
                </Select>
                <p className="text-sm text-muted-foreground">
                  Langue de l'interface utilisateur
                </p>
              </div>

              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label>Animations</Label>
                  <p className="text-sm text-muted-foreground">
                    Activer les animations d'interface
                  </p>
                </div>
                <Switch
                  checked={settings.animations}
                  onCheckedChange={handleAnimationsChange}
                  disabled={saving}
                />
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Onglet Sécurité */}
        <TabsContent value="security" className="space-y-4" forceMount>
          <Card>
            <CardHeader>
              <CardTitle>Sécurité du compte</CardTitle>
              <CardDescription>
                Gérez les sessions actives et la sécurité de votre compte
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">
                    Gérer vos sessions actives : terminez des sessions
                    individuelles ou toutes les autres sessions à distance.
                  </p>
                </div>
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    onClick={() => setShowDeleteAllDialog(true)}
                  >
                    <LogOut className="w-4 h-4 mr-2" />
                    Terminer les autres sessions
                  </Button>
                  {/* Delete all dialog */}
                  <Dialog
                    open={showDeleteAllDialog}
                    onOpenChange={setShowDeleteAllDialog}
                  >
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Terminer les autres sessions</DialogTitle>
                        <DialogDescription>
                          Voulez-vous vraiment terminer toutes les autres
                          sessions actives ? Cette action déconnectera vos
                          sessions sur d'autres appareils.
                        </DialogDescription>
                      </DialogHeader>
                      <div className="py-4">
                        <p className="text-sm text-muted-foreground">
                          Sessions trouvées :{" "}
                          {sessions.filter((s) => !s.isCurrent).length}
                        </p>
                      </div>
                      <DialogFooter>
                        <Button
                          variant="ghost"
                          onClick={() => setShowDeleteAllDialog(false)}
                        >
                          Annuler
                        </Button>
                        <Button
                          onClick={async () => {
                            setDeletingAll(true);
                            try {
                              const res = await fetch("/api/v1/sessions", {
                                method: "DELETE",
                              });
                              const json = await res.json();
                              if (json.success) {
                                toast.success("Succès", {
                                  description:
                                    json.data?.message || "Sessions terminées",
                                });
                                await fetchSessions();
                                setShowDeleteAllDialog(false);
                              } else {
                                throw new Error(
                                  json.error || "Erreur lors de la suppression",
                                );
                              }
                            } catch (err) {
                              toast.error("Erreur", {
                                description:
                                  err instanceof Error
                                    ? err.message
                                    : String(err),
                              });
                            } finally {
                              setDeletingAll(false);
                            }
                          }}
                          disabled={deletingAll}
                        >
                          {deletingAll
                            ? "Suppression..."
                            : "Terminer les autres"}
                        </Button>
                      </DialogFooter>
                    </DialogContent>
                  </Dialog>
                </div>
              </div>

              <div>
                {/* Sessions list */}
                <SessionsList />
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Onglet Préférences */}
        <TabsContent value="preferences" className="space-y-4" forceMount>
          <Card>
            <CardHeader>
              <CardTitle>Préférences métier</CardTitle>
              <CardDescription>
                Configurez les paramètres par défaut pour votre activité
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <Label>Devise par défaut</Label>
                <Select
                  value={settings.preferences.currency}
                  onValueChange={(value) =>
                    handlePreferenceChange("currency", value)
                  }
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="EUR">Euro (€)</SelectItem>
                    <SelectItem value="USD">Dollar US ($)</SelectItem>
                    <SelectItem value="CNY">Yuan chinois (¥)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Unité de poids</Label>
                <Select
                  value={settings.preferences.weightUnit}
                  onValueChange={(value) =>
                    handlePreferenceChange("weightUnit", value)
                  }
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="g">Grammes (g)</SelectItem>
                    <SelectItem value="kg">Kilogrammes (kg)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Format de date</Label>
                <Select
                  value={settings.preferences.dateFormat}
                  onValueChange={(value) =>
                    handlePreferenceChange("dateFormat", value)
                  }
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="DD/MM/YYYY">JJ/MM/AAAA</SelectItem>
                    <SelectItem value="MM/DD/YYYY">MM/JJ/AAAA</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label>Taux de change automatique</Label>
                  <p className="text-sm text-muted-foreground">
                    Mise à jour automatique des taux de change
                  </p>
                </div>
                <Switch
                  checked={settings.preferences.autoExchangeRate}
                  onCheckedChange={(value) =>
                    handlePreferenceChange("autoExchangeRate", value)
                  }
                />
              </div>

              <Button
                onClick={savePreferences}
                disabled={saving}
                className="w-full"
              >
                {saving ? "Sauvegarde..." : "Sauvegarder les préférences"}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Onglet Intégrations */}
        <TabsContent value="integrations" className="space-y-4">
          <SuperbuyConnect />

          <Card>
            <CardHeader>
              <CardTitle>Autres Intégrations</CardTitle>
              <CardDescription>
                Connectez vos comptes marketplace pour une synchronisation
                automatique
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="flex flex-col items-center justify-center py-12 text-center">
                <Plug className="h-12 w-12 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">
                  Bientôt disponible
                </h3>
                <p className="text-sm text-muted-foreground max-w-md">
                  Les intégrations avec Vinted, eBay et d'autres
                  plateformes seront bientôt disponibles.
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

      </Tabs>
      {/* Single session delete dialog */}
      <Dialog
        open={!!sessionToDelete}
        onOpenChange={(open) => {
          if (!open) setSessionToDelete(null);
        }}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Terminer la session</DialogTitle>
            <DialogDescription>
              Vous êtes sur le point de terminer la session suivante. Cette
              action est irréversible.
            </DialogDescription>
          </DialogHeader>
          {sessionToDelete && (
            <div className="py-4">
              <div className="font-medium">
                {sessionToDelete.deviceName || "Inconnu"}
              </div>
              <div className="text-sm text-muted-foreground">
                {sessionToDelete.deviceType} • {sessionToDelete.ipAddress}
              </div>
              <div className="text-xs text-muted-foreground">
                Dernière activité:{" "}
                {sessionToDelete.lastActivityAt
                  ? new Date(sessionToDelete.lastActivityAt).toLocaleString()
                  : "Inconnue"}
              </div>
            </div>
          )}
          <DialogFooter>
            <Button variant="ghost" onClick={() => setSessionToDelete(null)}>
              Annuler
            </Button>
            <Button
              onClick={confirmDeleteSession}
              disabled={!!deletingSessionId}
            >
              {deletingSessionId ? "Suppression..." : "Terminer la session"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

import { BaseService } from "./base-service";
import { serviceContainer } from "./container";
import { User } from "@/lib/database/schema";
import { products, parcels } from "@/lib/database/schema";
import { eq } from "drizzle-orm";

export class UserService extends BaseService {
    constructor() {
        super("UserService");
    }

    /**
     * Get full user profile with statistics
     */
    async getProfile(userId: string) {
        return this.executeOperation("getProfile", async () => {
            const userRepository = serviceContainer.getUserRepository();
            const productRepository = serviceContainer.getProductRepository();
            const parcelleRepository = serviceContainer.getParcelRepository();

            const user = await userRepository.findById(userId);
            if (!user) {
                throw new Error("User not found");
            }

            // Get statistics
            const totalProducts = await productRepository.count({ where: eq(products.userId, userId) });
            const totalParcelles = await parcelleRepository.count({ where: eq(parcels.userId, userId) });

            // Calculate active days
            const createdDate = new Date(user.createdAt);
            const today = new Date();
            const daysActive = Math.floor(
                (today.getTime() - createdDate.getTime()) / (1000 * 60 * 60 * 24)
            );

            return {
                id: user.id,
                username: user.username,
                email: user.email,
                bio: user.bio,
                avatar: user.avatar,
                language: user.language,
                theme: user.theme,
                role: user.role,
                lastLoginAt: user.lastLoginAt,
                createdAt: user.createdAt,
                updatedAt: user.updatedAt,
                stats: {
                    totalProducts,
                    totalParcels: totalParcelles,
                    daysActive,
                    totalAnalyses: 0, // Feature removed
                },
            };
        });
    }

    /**
     * Update user profile
     */
    async updateProfile(userId: string, data: Partial<Pick<User, "username" | "email" | "bio" | "avatar" | "language" | "theme">>) {
        return this.executeOperation("updateProfile", async () => {
            const userRepository = serviceContainer.getUserRepository();

            const updatedUser = await userRepository.updateProfile(userId, data);
            if (!updatedUser) {
                throw new Error("Failed to update profile");
            }

            return {
                id: updatedUser.id,
                username: updatedUser.username,
                email: updatedUser.email,
                bio: updatedUser.bio,
                avatar: updatedUser.avatar,
                language: updatedUser.language,
                theme: updatedUser.theme,
            };
        });
    }

    /**
     * Get user settings
     */
    async getSettings(userId: string) {
        return this.executeOperation("getSettings", async () => {
            const userRepository = serviceContainer.getUserRepository();
            const user = await userRepository.findById(userId);

            if (!user) {
                throw new Error("User not found");
            }

            // Parse preferences
            const preferences = typeof user.preferences === "string"
                ? JSON.parse(user.preferences || "{}")
                : user.preferences || {};

            return {
                theme: user.theme || "system",
                language: user.language || "fr",
                animations: preferences.animations ?? true,
                preferences: {
                    currency: preferences.currency || "EUR",
                    weightUnit: preferences.weightUnit || "g",
                    dateFormat: preferences.dateFormat || "DD/MM/YYYY",
                    autoExchangeRate: preferences.autoExchangeRate ?? true,
                },
            };
        });
    }

    /**
     * Update user settings
     */
    async updateSettings(userId: string, data: {
        theme?: string;
        language?: string;
        animations?: boolean;
        preferences?: Record<string, unknown>
    }) {
        return this.executeOperation("updateSettings", async () => {
            const userRepository = serviceContainer.getUserRepository();
            const user = await userRepository.findById(userId);

            if (!user) {
                throw new Error("User not found");
            }

            const currentPreferences = typeof user.preferences === "string"
                ? JSON.parse(user.preferences || "{}")
                : user.preferences || {};

            // Merge preferences
            const updatedPreferences = {
                ...currentPreferences,
                ...(data.preferences || {}),
            };

            if (data.animations !== undefined) {
                updatedPreferences.animations = data.animations;
            }

            // Prepare update data
            const updateData: any = {};
            if (data.theme) updateData.theme = data.theme;
            if (data.language) updateData.language = data.language;

            // Update user
            // We need to update preferences field manually as updateProfile only handles specific fields
            // So we use the base repository update method via a custom query or expose a method in UserRepository
            // But UserRepository.updateProfile is limited.
            // Let's use UserRepository.update which is inherited from BaseRepository

            await userRepository.update(userId, {
                ...updateData,
                preferences: JSON.stringify(updatedPreferences),
            });

            return { message: "Settings updated successfully" };
        });
    }
}
